import React, {Component} from 'react';
import { Form,Button,Container,Header } from "semantic-ui-react";
import ReactDOM from "react-dom";


let web3 = require('../utils/initWeb3')
let {votingFactoryContractInstance, votingABI} = require('../eth/lotteryInstance')

class VoteNow extends Component {
    constructor(props) {
        super(props)
        console.log(this.props.index)
        this.state = {
            voteOptions: [],
            voteTitle: '',
            voteExplain: '',
        }

    }

    //内置钩子函数，在页面渲染之后自动调用
    async componentWillMount() {
        let voteAllCreatorAddresses = await votingFactoryContractInstance.methods.getAllCreatorVotings().call()
        let nowVoteAddress = voteAllCreatorAddresses[this.props.index]
        let _newVotingContractInstance = new web3.eth.Contract(votingABI);
        _newVotingContractInstance.options.address = nowVoteAddress;

        let _voteOptions = await _newVotingContractInstance.methods.getVoteOptions().call()
        let _voteTitle = await _newVotingContractInstance.methods.voteTitle().call()
        let _voteExplain = await _newVotingContractInstance.methods.voteExplain().call()

        this.setState({
            voteOptions: _voteOptions,
            voteTitle: _voteTitle,
            voteExplain: _voteExplain
        })
        console.log("11111111111")
        console.log(this.state.voteOptions)
        console.log(this.state.voteTitle)
        console.log(this.state.voteExplain)
        console.log("11111111111")
    }

    handleChange = (e, { value }) => {
        this.setState({ value })
        console.log(this.state.value)
    }

    handleSubmit = () => {
        console.log("11111111111")
    }


    //卸载钩子函数
    // componentDidMount


    render() {
        let lists = this.state.voteOptions.map((option,index) => {
            if(index % 2 === 0 && this.state.voteOptions.length-1 !== index){
                return(
                    <Form.Group key={index} widths='equal'>
                        <Form.Radio
                            label= {this.state.voteOptions[index]}
                            value= {this.state.voteOptions[index]}
                            checked={this.state.value === this.state.voteOptions[index]}
                            onChange={this.handleChange}
                        />
                        <Form.Radio
                            label= {this.state.voteOptions[index+1]}
                            value= {this.state.voteOptions[index+1]}
                            checked={this.state.value === this.state.voteOptions[index+1]}
                            onChange={this.handleChange}
                        />
                    </Form.Group>
                )
            }else if(index % 2 === 0 && this.state.voteOptions.length-1 === index){
                return(
                    <Form.Group key={index} widths='equal'>
                        <Form.Radio
                            label= {this.state.voteOptions[index]}
                            value= {this.state.voteOptions[index]}
                            checked={this.state.value === this.state.voteOptions[index]}
                            onChange={this.handleChange}
                        />
                    </Form.Group>
                )
            }
        })
        return (
            <div className="row">
                <div className="sixteen wide column">
                    <div className="ui segments">
                        <div className="ui segment">
                            <Container textAlign='center' text>
                    <Header as='h2'>{this.state.voteTitle}</Header>
                    <p>{this.state.voteExplain}</p>

                    <Form>
                        {
                            lists
                        }
                        <Form.Group widths='equal'>
                            <Form.Field label='ciphertext' control='textarea' rows='3' />
                            <Form.Field label='ciphertext' control='textarea' rows='3' />
                        </Form.Group>
                        <Form.Group widths='equal'>
                            <Button basic>encryption</Button>
                            <Button basic>autograph</Button>
                            <Button basic onClick = {this.handleSubmit}>Submit</Button>
                        </Form.Group>
                        <Button positive onClick = {this.handleSubmit}>Submit</Button>
                    </Form>
                </Container>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default VoteNow;
